contracts/CrossChainMarketplace.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.17;

import "@openzeppelin/contracts/token/ERC721/IERC721.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/security/Pausable.sol";
import "./XCMBridge.sol";

/**
 * @title CrossChainMarketplace
 * @dev Cross-chain NFT marketplace with XCM integration
 * Enables NFT trading across Polkadot Hub and Moonbase Alpha
 */
contract CrossChainMarketplace is Ownable, ReentrancyGuard, Pausable {
    // Marketplace listing structure
    struct Listing {
        address seller;
        address nftContract;
        uint256 tokenId;
        uint256 price;
        address paymentToken; // address(0) for native token
        uint32 sourceChainId;
        bool isActive;
        bool isCrossChain;
        uint256 timestamp;
    }

    // Cross-chain purchase structure
    struct CrossChainPurchase {
        bytes32 listingId;
        address buyer;
        uint256 price;
        address paymentToken;
        uint32 sourceChainId;
        uint32 destinationChainId;
        bool completed;
        uint256 timestamp;
    }

    // XCM Bridge reference
    XCMBridge public xcmBridge;

    // State variables
    mapping(bytes32 => Listing) public listings;
    mapping(bytes32 => CrossChainPurchase) public crossChainPurchases;
    mapping(address => bool) public supportedPaymentTokens;
    mapping(uint32 => bool) public supportedChains;

    // Fee configuration
    uint256 public marketplaceFee = 250; // 2.5% in basis points
    uint256 public constant MAX_FEE = 1000; // 10% maximum fee
    address public feeRecipient;

    // Events
    event NFTListed(
        bytes32 indexed listingId,
        address indexed seller,
        address indexed nftContract,
        uint256 tokenId,
        uint256 price,
        address paymentToken,
        bool isCrossChain
    );

    event NFTSold(
        bytes32 indexed listingId,
        address indexed buyer,
        address indexed seller,
        uint256 price,
        address paymentToken
    );

    event CrossChainPurchaseInitiated(
        bytes32 indexed purchaseId,
        bytes32 indexed listingId,
        address indexed buyer,
        uint32 sourceChainId,
        uint32 destinationChainId
    );

    event CrossChainPurchaseCompleted(
        bytes32 indexed purchaseId,
        address indexed buyer,
        address indexed seller
    );

    event ListingCancelled(bytes32 indexed listingId, address indexed seller);

    constructor(address _xcmBridge, address _feeRecipient) {
        xcmBridge = XCMBridge(_xcmBridge);
        feeRecipient = _feeRecipient;

        // Initialize supported chains
        supportedChains[420420422] = true; // Polkadot Hub
        supportedChains[1287] = true; // Moonbase Alpha

        // Support native token payments
        supportedPaymentTokens[address(0)] = true;
    }

    /**
     * @dev List NFT for sale
     * @param nftContract NFT contract address
     * @param tokenId Token ID
     * @param price Listing price
     * @param paymentToken Payment token address (address(0) for native)
     * @param isCrossChain Whether this is a cross-chain listing
     */
    function listNFT(
        address nftContract,
        uint256 tokenId,
        uint256 price,
        address paymentToken,
        bool isCrossChain
    ) external nonReentrant whenNotPaused {
        require(price > 0, "Price must be greater than 0");
        require(supportedPaymentTokens[paymentToken], "Payment token not supported");
        require(
            IERC721(nftContract).ownerOf(tokenId) == msg.sender,
            "Not token owner"
        );
        require(
            IERC721(nftContract).isApprovedForAll(msg.sender, address(this)) ||
            IERC721(nftContract).getApproved(tokenId) == address(this),
            "Marketplace not approved"
        );

        bytes32 listingId = keccak256(
            abi.encodePacked(
                nftContract,
                tokenId,
                msg.sender,
                block.timestamp,
                block.chainid
            )
        );

        listings[listingId] = Listing({
            seller: msg.sender,
            nftContract: nftContract,
            tokenId: tokenId,
            price: price,
            paymentToken: paymentToken,
            sourceChainId: uint32(block.chainid),
            isActive: true,
            isCrossChain: isCrossChain,
            timestamp: block.timestamp
        });

        emit NFTListed(
            listingId,
            msg.sender,
            nftContract,
            tokenId,
            price,
            paymentToken,
            isCrossChain
        );
    }

    /**
     * @dev Purchase NFT (same chain)
     * @param listingId Listing ID
     */
    function purchaseNFT(bytes32 listingId) external payable nonReentrant whenNotPaused {
        Listing storage listing = listings[listingId];
        require(listing.isActive, "Listing not active");
        require(!listing.isCrossChain, "Use cross-chain purchase for cross-chain listings");
        require(listing.sourceChainId == uint32(block.chainid), "Wrong chain");

        uint256 totalPrice = listing.price;
        uint256 fee = (totalPrice * marketplaceFee) / 10000;
        uint256 sellerAmount = totalPrice - fee;

        // Handle payment
        if (listing.paymentToken == address(0)) {
            require(msg.value >= totalPrice, "Insufficient payment");

            // Transfer fee to fee recipient
            if (fee > 0) {
                (bool feeSuccess, ) = payable(feeRecipient).call{value: fee}("");
                require(feeSuccess, "Fee transfer failed");
            }

            // Transfer payment to seller
            (bool sellerSuccess, ) = payable(listing.seller).call{value: sellerAmount}("");
            require(sellerSuccess, "Seller payment failed");

            // Refund excess payment
            if (msg.value > totalPrice) {
                (bool refundSuccess, ) = payable(msg.sender).call{value: msg.value - totalPrice}("");
                require(refundSuccess, "Refund failed");
            }
        } else {
            IERC20 paymentToken = IERC20(listing.paymentToken);
            require(
                paymentToken.transferFrom(msg.sender, address(this), totalPrice),
                "Payment transfer failed"
            );

            // Transfer fee to fee recipient
            if (fee > 0) {
                require(
                    paymentToken.transfer(feeRecipient, fee),
                    "Fee transfer failed"
                );
            }

            // Transfer payment to seller
            require(
                paymentToken.transfer(listing.seller, sellerAmount),
                "Seller payment failed"
            );
        }

        // Transfer NFT to buyer
        IERC721(listing.nftContract).safeTransferFrom(
            listing.seller,
            msg.sender,
            listing.tokenId
        );

        // Mark listing as inactive
        listing.isActive = false;

        emit NFTSold(
            listingId,
            msg.sender,
            listing.seller,
            totalPrice,
            listing.paymentToken
        );
    }

    /**
     * @dev Initiate cross-chain NFT purchase
     * @param listingId Listing ID
     * @param destinationChainId Target chain for NFT delivery
     */
    function initiateCrossChainPurchase(
        bytes32 listingId,
        uint32 destinationChainId
    ) external payable nonReentrant whenNotPaused {
        Listing storage listing = listings[listingId];
        require(listing.isActive, "Listing not active");
        require(listing.isCrossChain, "Not a cross-chain listing");
        require(supportedChains[destinationChainId], "Destination chain not supported");

        uint256 totalPrice = listing.price;
        uint256 fee = (totalPrice * marketplaceFee) / 10000;
        uint256 sellerAmount = totalPrice - fee;

        // Handle payment (similar to regular purchase)
        if (listing.paymentToken == address(0)) {
            require(msg.value >= totalPrice, "Insufficient payment");

            if (fee > 0) {
                payable(feeRecipient).transfer(fee);
            }

            payable(listing.seller).transfer(sellerAmount);

            if (msg.value > totalPrice) {
                payable(msg.sender).transfer(msg.value - totalPrice);
            }
        } else {
            IERC20 paymentToken = IERC20(listing.paymentToken);
            require(
                paymentToken.transferFrom(msg.sender, address(this), totalPrice),
                "Payment transfer failed"
            );

            if (fee > 0) {
                require(
                    paymentToken.transfer(feeRecipient, fee),
                    "Fee transfer failed"
                );
            }

            require(
                paymentToken.transfer(listing.seller, sellerAmount),
                "Seller payment failed"
            );
        }

        bytes32 purchaseId = keccak256(
            abi.encodePacked(
                listingId,
                msg.sender,
                destinationChainId,
                block.timestamp
            )
        );

        crossChainPurchases[purchaseId] = CrossChainPurchase({
            listingId: listingId,
            buyer: msg.sender,
            price: totalPrice,
            paymentToken: listing.paymentToken,
            sourceChainId: listing.sourceChainId,
            destinationChainId: destinationChainId,
            completed: false,
            timestamp: block.timestamp
        });

        // Lock NFT in XCM bridge for cross-chain transfer
        IERC721(listing.nftContract).approve(address(xcmBridge), listing.tokenId);
        xcmBridge.lockNFT(listing.nftContract, listing.tokenId, destinationChainId);

        // Mark listing as inactive
        listing.isActive = false;

        emit CrossChainPurchaseInitiated(
            purchaseId,
            listingId,
            msg.sender,
            listing.sourceChainId,
            destinationChainId
        );
    }

    /**
     * @dev Complete cross-chain purchase (called by XCM bridge)
     * @param purchaseId Purchase ID
     */
    function completeCrossChainPurchase(bytes32 purchaseId) external {
        require(msg.sender == address(xcmBridge), "Only XCM bridge can complete");

        CrossChainPurchase storage purchase = crossChainPurchases[purchaseId];
        require(!purchase.completed, "Purchase already completed");

        purchase.completed = true;

        emit CrossChainPurchaseCompleted(
            purchaseId,
            purchase.buyer,
            listings[purchase.listingId].seller
        );
    }

    /**
     * @dev Cancel listing
     * @param listingId Listing ID
     */
    function cancelListing(bytes32 listingId) external nonReentrant {
        Listing storage listing = listings[listingId];
        require(listing.seller == msg.sender, "Not listing owner");
        require(listing.isActive, "Listing not active");

        listing.isActive = false;

        emit ListingCancelled(listingId, msg.sender);
    }

    /**
     * @dev Set marketplace fee
     * @param _fee Fee in basis points
     */
    function setMarketplaceFee(uint256 _fee) external onlyOwner {
        require(_fee <= MAX_FEE, "Fee too high");
        marketplaceFee = _fee;
    }

    /**
     * @dev Set fee recipient
     * @param _feeRecipient New fee recipient address
     */
    function setFeeRecipient(address _feeRecipient) external onlyOwner {
        require(_feeRecipient != address(0), "Invalid fee recipient");
        feeRecipient = _feeRecipient;
    }

    /**
     * @dev Set payment token support
     * @param token Token address
     * @param supported Support status
     */
    function setPaymentTokenSupport(address token, bool supported) external onlyOwner {
        supportedPaymentTokens[token] = supported;
    }

    /**
     * @dev Set chain support
     * @param chainId Chain ID
     * @param supported Support status
     */
    function setChainSupport(uint32 chainId, bool supported) external onlyOwner {
        supportedChains[chainId] = supported;
    }

    /**
     * @dev Get listing information
     * @param listingId Listing ID
     * @return Listing information
     */
    function getListing(bytes32 listingId) external view returns (Listing memory) {
        return listings[listingId];
    }

    /**
     * @dev Get cross-chain purchase information
     * @param purchaseId Purchase ID
     * @return CrossChainPurchase information
     */
    function getCrossChainPurchase(bytes32 purchaseId) 
        external 
        view 
        returns (CrossChainPurchase memory) 
    {
        return crossChainPurchases[purchaseId];
    }

    /**
     * @dev Emergency pause
     */
    function pause() external onlyOwner {
        _pause();
    }

    /**
     * @dev Unpause
     */
    function unpause() external onlyOwner {
        _unpause();
    }
}

contracts/XCMBridge.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.17;

import "@openzeppelin/contracts/token/ERC721/IERC721.sol";
import "@openzeppelin/contracts/token/ERC721/IERC721Receiver.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/security/Pausable.sol";

/**
 * @title XCMBridge
 * @dev Cross-chain NFT bridge contract for Polkadot XCM integration
 * Handles NFT locking/unlocking and cross-chain message processing
 */
contract XCMBridge is IERC721Receiver, Ownable, ReentrancyGuard, Pausable {
    // XCM message types
    enum MessageType {
        LOCK_NFT,
        UNLOCK_NFT,
        MINT_WRAPPED_NFT,
        BURN_WRAPPED_NFT
    }

    // Cross-chain NFT information
    struct CrossChainNFT {
        address originalContract;
        uint256 originalTokenId;
        address originalOwner;
        uint32 sourceChainId;
        uint32 destinationChainId;
        bool isLocked;
        uint256 timestamp;
    }

    // XCM message structure
    struct XCMMessage {
        MessageType messageType;
        address nftContract;
        uint256 tokenId;
        address recipient;
        uint32 sourceChainId;
        uint32 destinationChainId;
        bytes32 messageHash;
        bool processed;
    }

    // Chain ID mappings
    uint32 public constant POLKADOT_HUB_CHAIN_ID = 420420422;
    uint32 public constant MOONBASE_ALPHA_CHAIN_ID = 1287;

    // State variables
    mapping(bytes32 => CrossChainNFT) public crossChainNFTs;
    mapping(bytes32 => XCMMessage) public xcmMessages;
    mapping(address => bool) public authorizedContracts;
    mapping(uint32 => bool) public supportedChains;
    mapping(address => mapping(uint256 => bytes32)) public nftToMessageHash;

    // Events
    event NFTLocked(
        address indexed nftContract,
        uint256 indexed tokenId,
        address indexed owner,
        uint32 destinationChainId,
        bytes32 messageHash
    );

    event NFTUnlocked(
        address indexed nftContract,
        uint256 indexed tokenId,
        address indexed owner,
        bytes32 messageHash
    );

    event XCMMessageSent(
        bytes32 indexed messageHash,
        MessageType messageType,
        uint32 destinationChainId
    );

    event XCMMessageReceived(
        bytes32 indexed messageHash,
        MessageType messageType,
        uint32 sourceChainId
    );

    event ChainSupported(uint32 chainId, bool supported);
    event ContractAuthorized(address indexed nftContract, bool authorized);

    constructor() {
        // Initialize supported chains
        supportedChains[POLKADOT_HUB_CHAIN_ID] = true;
        supportedChains[MOONBASE_ALPHA_CHAIN_ID] = true;
    }

    /**
     * @dev Lock NFT for cross-chain transfer
     * @param nftContract Address of the NFT contract
     * @param tokenId Token ID to lock
     * @param destinationChainId Target chain ID
     */
    function lockNFT(
        address nftContract,
        uint256 tokenId,
        uint32 destinationChainId
    ) external nonReentrant whenNotPaused {
        require(authorizedContracts[nftContract], "Contract not authorized");
        require(supportedChains[destinationChainId], "Chain not supported");
        require(
            IERC721(nftContract).ownerOf(tokenId) == msg.sender,
            "Not token owner"
        );

        // Generate unique message hash
        bytes32 messageHash = keccak256(
            abi.encodePacked(
                nftContract,
                tokenId,
                msg.sender,
                block.chainid,
                destinationChainId,
                block.timestamp
            )
        );

        // Transfer NFT to bridge contract
        IERC721(nftContract).safeTransferFrom(
            msg.sender,
            address(this),
            tokenId
        );

        // Store cross-chain NFT information
        crossChainNFTs[messageHash] = CrossChainNFT({
            originalContract: nftContract,
            originalTokenId: tokenId,
            originalOwner: msg.sender,
            sourceChainId: uint32(block.chainid),
            destinationChainId: destinationChainId,
            isLocked: true,
            timestamp: block.timestamp
        });

        // Create XCM message
        xcmMessages[messageHash] = XCMMessage({
            messageType: MessageType.LOCK_NFT,
            nftContract: nftContract,
            tokenId: tokenId,
            recipient: msg.sender,
            sourceChainId: uint32(block.chainid),
            destinationChainId: destinationChainId,
            messageHash: messageHash,
            processed: false
        });

        emit NFTLocked(nftContract, tokenId, msg.sender, destinationChainId, messageHash);
        emit XCMMessageSent(messageHash, MessageType.LOCK_NFT, destinationChainId);
    }

    /**
     * @dev Unlock NFT after cross-chain transfer completion
     * @param messageHash Hash of the original lock message
     */
    function unlockNFT(bytes32 messageHash) external nonReentrant whenNotPaused {
        CrossChainNFT storage nftInfo = crossChainNFTs[messageHash];
        require(nftInfo.isLocked, "NFT not locked");
        require(nftInfo.originalOwner == msg.sender, "Not original owner");

        // Mark as unlocked
        nftInfo.isLocked = false;

        // Transfer NFT back to original owner
        IERC721(nftInfo.originalContract).safeTransferFrom(
            address(this),
            nftInfo.originalOwner,
            nftInfo.originalTokenId
        );

        emit NFTUnlocked(
            nftInfo.originalContract,
            nftInfo.originalTokenId,
            nftInfo.originalOwner,
            messageHash
        );
    }

    /**
     * @dev Process incoming XCM message
     * @param messageHash Hash of the XCM message
     * @param messageType Type of XCM message
     * @param nftContract NFT contract address
     * @param tokenId Token ID
     * @param recipient Recipient address
     * @param sourceChainId Source chain ID
     */
    function processXCMMessage(
        bytes32 messageHash,
        MessageType messageType,
        address nftContract,
        uint256 tokenId,
        address recipient,
        uint32 sourceChainId
    ) external nonReentrant whenNotPaused {
        require(!xcmMessages[messageHash].processed, "Message already processed");
        require(supportedChains[sourceChainId], "Source chain not supported");
        require(authorizedContracts[nftContract], "Contract not authorized");

        xcmMessages[messageHash] = XCMMessage({
            messageType: messageType,
            nftContract: nftContract,
            tokenId: tokenId,
            recipient: recipient,
            sourceChainId: sourceChainId,
            destinationChainId: uint32(block.chainid),
            messageHash: messageHash,
            processed: true
        });

        emit XCMMessageReceived(messageHash, messageType, sourceChainId);
    }

    /**
     * @dev Authorize NFT contract for cross-chain transfers
     * @param nftContract Address of the NFT contract
     * @param authorized Authorization status
     */
    function setContractAuthorization(
        address nftContract,
        bool authorized
    ) external onlyOwner {
        authorizedContracts[nftContract] = authorized;
        emit ContractAuthorized(nftContract, authorized);
    }

    /**
     * @dev Set chain support status
     * @param chainId Chain ID
     * @param supported Support status
     */
    function setChainSupport(uint32 chainId, bool supported) external onlyOwner {
        supportedChains[chainId] = supported;
        emit ChainSupported(chainId, supported);
    }

    /**
     * @dev Get cross-chain NFT information
     * @param messageHash Message hash
     * @return CrossChainNFT information
     */
    function getCrossChainNFT(bytes32 messageHash)
        external
        view
        returns (CrossChainNFT memory)
    {
        return crossChainNFTs[messageHash];
    }

    /**
     * @dev Get XCM message information
     * @param messageHash Message hash
     * @return XCMMessage information
     */
    function getXCMMessage(bytes32 messageHash)
        external
        view
        returns (XCMMessage memory)
    {
        return xcmMessages[messageHash];
    }

    /**
     * @dev Emergency pause function
     */
    function pause() external onlyOwner {
        _pause();
    }

    /**
     * @dev Unpause function
     */
    function unpause() external onlyOwner {
        _unpause();
    }

    /**
     * @dev Handle NFT reception
     */
    function onERC721Received(
        address,
        address,
        uint256,
        bytes calldata
    ) external pure override returns (bytes4) {
        return IERC721Receiver.onERC721Received.selector;
    }

    /**
     * @dev Emergency withdrawal function (only owner)
     * @param nftContract NFT contract address
     * @param tokenId Token ID
     * @param to Recipient address
     */
    function emergencyWithdraw(
        address nftContract,
        uint256 tokenId,
        address to
    ) external onlyOwner {
        IERC721(nftContract).safeTransferFrom(address(this), to, tokenId);
    }
}
